import { JsonLdDocumentTerm } from "./JsonLdDocumentTerm";
import { JsonLdDocumentContext } from "./JsonLdDocumentContext";
import { JsonLdObjectType } from "./JsonLdObjectType";
import { ContextResolver } from "./ContextResolver";
/**
 * Describes the processing context
 */
export interface JsonLdDocumentProcessingContext {
    /**
     * The type of JSON-LD object currently being processed
     */
    readonly currentJsonLdObjectType?: JsonLdObjectType;
    /**
     * Document being processed
     */
    readonly document: any;
    /**
     * The JSON-LD Document context for current document being processed
     */
    readonly jsonLdDocumentContext?: JsonLdDocumentContext;
    /**
     * An array of unmapped terms found in the JSON-LD document during initial processing
     */
    readonly unmappedTerms?: string[];
    /**
     * The current JSON-LD term being processed
     */
    readonly currentTerm?: JsonLdDocumentTerm;
    /**
     * JSON-LD context resolver
     */
    readonly contextResolver: ContextResolver;
}
